(E'#UserSubmitNewArticleText',E'default',E'<p>You cannot upload your preprint on this website. Your preprint must have been deposited on a preprint server or an open archive such as bioRxiv, Zenodo, arXiv, HAL, OSF preprints... and must have a DOI or a specific URL.</p><p><span style="color: red; font-weight: bold;">*</span> <span style="color: #000000;">Mandatory information</span></p>'),
(E'#UserSubmitNewArticleTitle',E'default',E'Submit your preprint'),
(E'#UserSuggestedRecommendersTitle',E'default',E'Suggested recommenders'),
(E'#WelcomingMessage',E'default',E'This search engine is devoted to look for manuscripts that have been recommended by {{description}}. Reviews, text of recommendations and manuscript information can be found here.\n\nOn this page, you may also submit a preprint manuscript you authored (not reviewed yet and deposited in an open archive).  \n\nMembers of {{description}} can also start a recommendation process.'),
(E'#SideBarSponsor',E'default',E'<p><img src="../static/images/inrae.jpg" alt="" width="155" height="70"></p>'),
(E'#SideBarIndex',E'default',E'<p><img src="../static/images/europe-pmc.jpg" alt="" data-mce-id="__mcenew" width="150" height="54"></p>\n<p><img src="../static/images/pubpeerlogo.png" alt="" data-mce-id="__mcenew" width="150" height="47"></p>\n<p><img src="../static/images/altmetric.jpg" alt="" data-mce-id="__mcenew" width="150" height="72"></p>\n<p><img src="../static/images/GSlogo.png" alt="" data-mce-id="__mcenew" width="150" height="38"></p>\n<p><img src="../static/images/openairelogo.png" alt="" data-mce-id="__mcenew" width="150" height="59"></p>\n<p><img src="../static/images/dimensionlogo.png" alt="" data-mce-id="__mcenew" width="150" height="36"></p>'),
(E'#SideBarManagingBoard',E'default',E'<p>Corina Logan<br>Anna Eklof<br>Julia Astegiano<br>Ben Phillips<br>Cyrille Gravel<br>Dominique Gravel<br>Tim Coulson<br>Francois Massol</p>'),
(E'#SideBarSupporters',E'default',E'<p><img src="../static/images/leuven.jpg" alt="" width="200" height="80"></p>\n<p><img src="../static/images/liege.jpg" alt="" width="200" height="80"></p>\n<p><img src="../static/images/sorbonne.jpg" alt="" width="200" height="80"></p>'),
(E'#NewFooterSubtext',E'default',E'<p><span style="color: #7e8c8d;">{{longname}} is the first community of the non-profit organization Peer Community In. The goal of Peer Community In is to promote scientific knowledge. Authors of comments, reviews and recommendations retain copyright under CC BY-ND. Contact &amp; credits. Publication director/directeur de la publication: Denis Bourguet. ISSN: {{issn}}.</span></p>');
