update mail_templates set contents = '
<p><strong>A friendly reminder of my invitation to review.</strong><span style="font-weight: 400;"> I hope you can accept, but if for any reason you must decline, please be assured that I understand your decision. In any case, a response - by clicking on the buttons below - would be greatly appreciated.<br></span><span style="font-weight: 400;">{{message}}</span></p>
<p>&nbsp;</p>'
, subject = '{{appName}}: Invitation from {{senderName}} to review a manuscript - reminder'
where hashtag = '#ReminderReviewerReviewInvitationNewUser';

