INSERT INTO "mail_templates"("hashtag","lang","subject","description","contents")
VALUES
(E'#ManagersPreprintResubmissionStage2',E'default',E'{{appName}}: Pre-submitted report now completed and ready for validation',E'Mail sent to managers on report resubmission',E'<div class="p-rich_text_section">Dear members of the Managing Board,</div>\n<div class="p-rich_text_section">&nbsp;</div>\n<div class="p-rich_text_section">This report submitted by {{submitterPerson}} entitled&nbsp;<strong data-stringify-type="bold">{{articleTitle}}</strong>&nbsp;that was previously in a Pre-submission stage is now ready to be validated.</div>\n<div class="p-rich_text_section">&nbsp;</div>\n<div class="p-rich_text_section">To check this submission, please follow this link <a href="{{linkTarget}}">{{linkTarget}}</a>.</div>\n<div class="p-rich_text_section">&nbsp;</div>\n<div class="p-rich_text_section"><strong data-stringify-type="bold">WARNING:</strong>&nbsp;<strong data-stringify-type="bold">do not validate it unless you (or someone else on the managing board) have checked the following points:</strong></div>\n<ul class="p-rich_text_list p-rich_text_list__bullet" data-stringify-type="unordered-list" data-indent="0" data-border="0">\n<li data-stringify-indent="0" data-stringify-border="0">Files are readable, lines are numbered</li>\n<li data-stringify-indent="0" data-stringify-border="0">DOI of (or permanent link to) the article, data, and script are correct,</li>\n<li data-stringify-indent="0" data-stringify-border="0">Data, scripts/code, and supplementary information are available</li>\n<li data-stringify-indent="0" data-stringify-border="0">A readme file is present with the data and script</li>\n<li data-stringify-indent="0" data-stringify-border="0">Mandatory sections are there ("funding", "Conflict of Interest disclosure", "Data, script, code, and supplementary information availability")</li>\n<li data-stringify-indent="0" data-stringify-border="0">No obvious financial conflicts of interest appear</li>\n</ul>\n<div class="p-rich_text_section">Have a nice day!</div>\n<p>&nbsp;</p>'),
(E'#ManagersPreprintResubmissionStage1',E'default',E'{{appName}}: Pre-submitted report now completed and ready for validation',E'Mail sent to managers on report resubmission',E'<div class="p-rich_text_section">Dear members of the Managing Board,</div>\n<div class="p-rich_text_section">&nbsp;</div>\n<div class="p-rich_text_section">This report submitted by {{submitterPerson}} entitled&nbsp;<strong data-stringify-type="bold">{{articleTitle}}</strong>&nbsp;that was previously in a Pre-submission stage is now ready to be validated.</div>\n<div class="p-rich_text_section">&nbsp;</div>\n<div class="p-rich_text_section">To check this submission, please follow this link <a href="{{linkTarget}}">{{linkTarget}}</a>.</div>\n<div class="p-rich_text_section">&nbsp;</div>\n<div class="p-rich_text_section"><strong data-stringify-type="bold">WARNING:</strong>&nbsp;<strong data-stringify-type="bold">do not validate it unless you (or someone else on the managing board) have checked the following points:</strong></div>\n<ul class="p-rich_text_list p-rich_text_list__bullet" data-stringify-type="unordered-list" data-indent="0" data-border="0">\n<li data-stringify-indent="0" data-stringify-border="0">Files are readable, lines are numbered</li>\n<li data-stringify-indent="0" data-stringify-border="0">DOI of (or permanent link to) the article, data, and script are correct,</li>\n<li data-stringify-indent="0" data-stringify-border="0">Data, scripts/code, and supplementary information are available</li>\n<li data-stringify-indent="0" data-stringify-border="0">A readme file is present with the data and script</li>\n<li data-stringify-indent="0" data-stringify-border="0">Mandatory sections are there ("funding", "Conflict of Interest disclosure", "Data, script, code, and supplementary information availability")</li>\n<li data-stringify-indent="0" data-stringify-border="0">No obvious financial conflicts of interest appear</li>\n</ul>\n<div class="p-rich_text_section">Have a nice day!</div>\n<p>&nbsp;</p>');
